INSERT INTO TUMOR_MARKER (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'GST-Pi','Glutathione S-Transferase Pi','GST-pi is the anionic isozyme of glutathione S-transferase.  GST enzymes play an important role in detoxification by conjugating hydrophobic and electrophilic compounds with reduced glutathione.  GST-pi appears to be present in all tissues, with the exception of red blood cells, and is over expressed in multidrug-resistant cells.  GST-pi may be fatty acid ethyl ester synthase III, a heart enzyme that nonoxidatively metabolizes ethanol.  (from OMIM 134660 and NCI)');
INSERT INTO TUMOR_MARKER (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'CEA','Carcinoembryonic Antigen','Carcinoembryonic antigen. A substance that is sometimes found in an increased amount in the blood of people with certain cancers.');
