INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Cystoscopy','Cystoscopy','(sist-OSS-ko-pee) Examination of the bladder and urethra using a thin, lighted instrument (called a cystoscope) inserted into the urethra. Tissue samples can be removed and examined under a microscope to determine whether disease is present.');
INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Bone Mineral Density','Bone Mineral Density','Various methods to measure how many grams of calcium and other bone minerals, collectively known as bone mineral content, are present in a segment of bone.');
