INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Aspirate, Us Guided','Aspirate, Ultrasound Guided','A technique in which high-frequency sound waves are bounced off internal organs and the echo pattern is converted into a 2 dimensional picture of the structures beneath the transducer.: (AS-pi-rit) Fluid withdrawn from a lump, often a cyst, or a nipple.');
INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Audiogram','Audiogram','Audiometric testing (hearing test) is conducted utilizing a manual or microprocessor audiometer. The test is usually carried out in a sound room or sound test chamber. Used to test for hearing loss.');
