INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Biopsy, Core Needle','Biopsy, Core Needle','The removal of a tissue sample using a needle with a relatively large diameter, for microscopic examination.');
