INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'BM Cellularity','Bone Marrow Cellularity','The soft, sponge-like tissue in the center of bones that produces white blood cells, red blood cells, and platelets.: The degree, quality, or condition of cells that are present. (On-line Medical Dictionary)');
INSERT INTO PROCEDURE_NAME (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION) VALUES(nextval('serial'),'Bone Spectometry','Bone Spectometry','A class of analytical techniques used to measure certain physical properties such as energy, mass and wavelength via the dispersion of radiant energy or particles into a spectrum.: Bone; a rigid connective tissue that makes up the skeleton of vertebrates.');
