INSERT INTO DOSE_FORM (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION,CONCEPT_ID,PUBLIC_ID) VALUES(nextval('serial'),'CEMENT','Cement Dosage Form','A substance that serves to produce solid union between two surfaces.','C45414','2579025');
INSERT INTO DOSE_FORM (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION,CONCEPT_ID,PUBLIC_ID) VALUES(nextval('serial'),'CONE','Cone Dosage Form','A solid dosage form bounded by a circular base and the surface formed by line segments joining every point of the boundary of the base to a common vertex. A cone (usually containing antibiotics) is normally placed below the gingiva after a dental extraction.','C42900','2579027');
INSERT INTO DOSE_FORM (IDENTIFIER,CODE,DISPLAY_NAME,DESCRIPTION,CONCEPT_ID,PUBLIC_ID) VALUES(nextval('serial'),'KIT','Kit Dosage Form','A packaged collection of related material.','C47916','2579079');
