INSERT INTO misc_documents (name,application,version,expiration_date,content) VALUES ('TrialCategory.Industrial','Registry','01',null,'The design and implementation of these clinical research studies is controlled by the pharmaceutical company.');
INSERT INTO misc_documents (name,application,version,expiration_date,content) VALUES ('TrialCategory.Institutional','Registry','01',null,'In-house clinical research studies authored or co-authored by Cancer Center investigators and undergoing scientific peer-review solely by the Protocol Review and Monitoring System of the Cancer Center. The Cancer Center investigator has primary responsibility for conceptualizing, designing and implementing the clinical research study and reporting results.\n\n<ul>\n  <LI>It is acceptable for industry and other entities to provide support (e.g., drug, device, other funding) but the trial should clearly be the intellectual product of the center investigator.</LI>\n <LI>This category may also include:\n       <ul>\n          <li> Institutional studies authored and implemented by investigators at another Center,</LI>\n              <LI>Multi-site institutional studies authored and implemented by investigators at your Center.</LI>\n       </ul>\n </LI>               \n</ul>');
INSERT INTO misc_documents (name,application,version,expiration_date,content) VALUES ('TrialCategory.National','Registry','01',null,'NCI National Clinical Trials Network (NCTN) and other NIH-supported National Trial Networks.<br>');
